import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const LandingPage = () => {
  const [feedback, setFeedback] = useState([]);

  useEffect(() => {
    fetchFeedback();
  }, []);

  const fetchFeedback = async () => {
    try {
      const response = await axios.get('/api/feedback/public');
      setFeedback(response.data);
    } catch (error) {
      console.error('Error fetching feedback:', error);
    }
  };

  const renderStars = (rating) => {
    return '★'.repeat(rating) + '☆'.repeat(5 - rating);
  };

  return (
    <div style={{ minHeight: '100vh', backgroundImage: 'url(/image.png)', backgroundSize: 'cover', backgroundPosition: 'center', backgroundAttachment: 'fixed' }}>
      {/* Navigation */}
      <nav style={{
        backgroundColor: '#ffffff',
        padding: '1rem 2rem',
        boxShadow: '0 4px 12px rgba(170, 141, 111, 0.15)',
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center'
      }}>
        <h1 style={{ margin: 0, color: '#aa8d6f' }}>DanceHub</h1>
        <div style={{ display: 'flex', gap: '1rem' }}>
          <Link to="/user-login" style={{
            padding: '0.5rem 1rem',
            backgroundColor: '#aa8d6f',
            color: '#ffffff',
            textDecoration: 'none',
            borderRadius: '4px',
            boxShadow: '0 2px 8px rgba(170, 141, 111, 0.3)'
          }}>
            Student Login
          </Link>
          <Link to="/admin-login" style={{
            padding: '0.5rem 1rem',
            backgroundColor: '#aa8d6f',
            color: '#ffffff',
            textDecoration: 'none',
            borderRadius: '4px',
            boxShadow: '0 2px 8px rgba(170, 141, 111, 0.3)'
          }}>
            Admin Login
          </Link>
        </div>
      </nav>
      <div className="background-image" style={{ backgroundImage: '', backgroundSize: 'cover', backgroundPosition: 'center', backgroundAttachment: 'fixed' }}>

      {/* Hero Section */}
      <section style={{
        textAlign: 'center',
        padding: '4rem 2rem',
        background: 'linear-gradient(135deg, #aa8d6f 0%, #c9a882 100%)',
        color: '#ffffff',
        boxShadow: '0 8px 24px rgba(170, 141, 111, 0.2)'
      }}>
        <h1 style={{ fontSize: '3rem', marginBottom: '1rem' }}>Welcome to DanceHub</h1>
        <p style={{ fontSize: '1.2rem', marginBottom: '2rem' }}>
          Your premier destination for dance classes and fitness programs
        </p>
        <Link to="/user-register" style={{
          padding: '1rem 2rem',
          backgroundColor: '#ffffff',
          color: '#aa8d6f',
          textDecoration: 'none',
          borderRadius: '8px',
          fontSize: '1.1rem',
          marginRight: '1rem',
          boxShadow: '0 4px 16px rgba(0, 0, 0, 0.15)'
        }}>
          Join as Student
        </Link>
        <Link to="/admin-register" style={{
          padding: '1rem 2rem',
          backgroundColor: '#ffeed3',
          color: '#aa8d6f',
          textDecoration: 'none',
          borderRadius: '8px',
          fontSize: '1.1rem',
          boxShadow: '0 4px 16px rgba(0, 0, 0, 0.15)'
        }}>
          Become Instructor
        </Link>
      </section>

      {/* Features Section */}
      <section style={{ padding: '4rem 2rem', backgroundImage: 'url(/image1.png)', backgroundSize: 'cover', backgroundPosition: 'center', backgroundAttachment: 'fixed' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '3rem', color: '#aa8d6f' }}>Why Choose DanceHub?</h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '2rem',
          maxWidth: '1200px',
          margin: '0 auto'
        }}>
          <div style={{
            backgroundColor: '#ffffff',
            padding: '2rem',
            borderRadius: '8px',
            boxShadow: '0 6px 20px rgba(170, 141, 111, 0.15)',
            textAlign: 'center'
          }}>
            <h3 style={{ color: '#aa8d6f', marginTop: 0 }}>Professional Instructors</h3>
            <p style={{ color: '#2c2c2c' }}>Learn from certified dance professionals with years of experience</p>
          </div>
          <div style={{
            backgroundColor: '#ffffff',
            padding: '2rem',
            borderRadius: '8px',
            boxShadow: '0 6px 20px rgba(170, 141, 111, 0.15)',
            textAlign: 'center'
          }}>
            <h3 style={{ color: '#aa8d6f', marginTop: 0 }}>Flexible Scheduling</h3>
            <p style={{ color: '#2c2c2c' }}>Choose from various time slots that fit your busy lifestyle</p>
          </div>
          <div style={{
            backgroundColor: '#ffffff',
            padding: '2rem',
            borderRadius: '8px',
            boxShadow: '0 6px 20px rgba(170, 141, 111, 0.15)',
            textAlign: 'center'
          }}>
            <h3 style={{ color: '#aa8d6f', marginTop: 0 }}>Track Progress</h3>
            <p style={{ color: '#2c2c2c' }}>Monitor your attendance and improvement with our tracking system</p>
          </div>
        </div>
      </section>

      {/* Testimonials */}
      <section style={{ padding: '4rem 2rem', backgroundImage: 'url(/image2.png)', backgroundSize: 'cover', backgroundPosition: 'center', backgroundAttachment: 'fixed' }}>
        <h2 style={{ textAlign: 'center', marginBottom: '3rem', color: '#aa8d6f' }}>What Our Students Say</h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '2rem',
          maxWidth: '1200px',
          margin: '0 auto'
        }}>
          {feedback.length > 0 ? feedback.map(item => (
            <div key={item._id} style={{
              backgroundColor: '#ffffff',
              padding: '2rem',
              borderRadius: '8px',
              boxShadow: '0 6px 20px rgba(170, 141, 111, 0.15)'
            }}>
              <div style={{
                color: '#ffc107',
                fontSize: '1.2rem',
                marginBottom: '1rem'
              }}>
                {renderStars(item.rating)}
              </div>
              <p style={{ fontStyle: 'italic', marginBottom: '1rem' }}>
                "{item.comment}"
              </p>
              <div style={{ fontWeight: 'bold', color: '#aa8d6f' }}>- {item.user?.name}</div>
              {item.class && (
                <div style={{ fontSize: '0.9rem', color: '#666', marginTop: '0.5rem' }}>
                  {item.class.name} Class
                </div>
              )}
            </div>
          )) : (
            <div style={{
              backgroundColor: '#ffffff',
              padding: '2rem',
              borderRadius: '8px',
              boxShadow: '0 6px 20px rgba(170, 141, 111, 0.15)',
              textAlign: 'center',
              gridColumn: '1 / -1'
            }}>
